#########################################################################
#  #7 - Powershell GUI - Jornada pela interface gráfica
#
#  Objetivos:
#     - Inserir imagem em botão
#     - Alterar a imagem do botão na ocorrência de eventos
#       como MouseHover (cursor sobre) ou MouseLeave (cursor deixar)
#     - Alternar entre texto e imagem do botão ao clique do mouse
#     - Demonstrar o uso de sons (padrão do SO; ou a partir de arquivos)
#
###################################################3#####################

Add-Type -AssemblyName System.Windows.Forms
Add-Type -AssemblyName System.Drawing
[Windows.Forms.Application]::EnableVisualStyles()


Function _Main {
    $Form7 = New-Object System.Windows.Forms.Form 
    $Form7.ClientSize = New-Object System.Drawing.Size(600,480) 
    $Form7.FormBorderStyle = [System.Windows.Forms.FormBorderStyle]::Fixed3D
    $Form7.Text = ".::. #7 Powershell GUI - Jornada pela interface gráfica .::."
    $Form7.MaximizeBox = $false
    $Form7.StartPosition = "CenterScreen"
    $Form7.BackColor = "#004A80"

    ########
    # ICONE
    $base64IconString = ""
    $iconimageBytes = [Convert]::FromBase64String($base64IconString)
    $ims = New-Object IO.MemoryStream($iconimageBytes, 0, $iconimageBytes.Length)
    $Form7.Icon = [System.Drawing.Icon]::FromHandle((new-object System.Drawing.Bitmap -argument $ims).GetHIcon())

    #
    # BotaoImg
    #
    $BotaoImg = New-Object System.Windows.Forms.Button
    $BotaoImg.Size = New-Object System.Drawing.Size(128, 128)
    $BotaoImg_X = ($Form7.ClientSize.Width / 2) - ($BotaoImg.Width / 2)
    $BotaoImg_Y = ($Form7.ClientSize.Height / 2) - ($BotaoImg.Height / 2)
    $BotaoImg.Location = New-Object System.Drawing.Point($BotaoImg_X, $BotaoImg_Y)
    $BotaoImg.BackColor = "White"
    $BotaoImg.Font = New-Object System.Drawing.Font("Arial", 18, [System.Drawing.FontStyle]::Bold)
    $BotaoImg.Text = $null

    #############################
    ### Cria Imagem para o Botão
    $base64Img01 = ""
    $imgBytes = [Convert]::FromBase64String($base64Img01)
    $imgStream01 = New-Object IO.MemoryStream($imgBytes, 0, $imgBytes.Length)
    $BotaoImg.Image = [System.Drawing.Image]::FromStream($imgStream01)
    $Form7.Controls.Add($BotaoImg)

    ### BotaoImg MouseHover Event Handler
    $BotaoImg.Add_MouseHover({
        BotaoImg_MouseHover
    })

    ### BotaoImg MouseLeave Event Handler
    $BotaoImg.Add_MouseLeave({
        BotaoImg_MouseLeave
    })

    ### BotaoImg Click Event Handler
    $BotaoImg.Add_Click({
        BotaoImg_Click
    })

    # Exibe o Form
    [System.Windows.Forms.Application]::Run($Form7)
 
} ### End _Main


  ### BotaoImg_MouseHover Event Handler
  Function BotaoImg_MouseHover() {
    If ($BotaoImg.Image -ne $null) {
        $base64Img02 = ""
        $imgBytes = [Convert]::FromBase64String($base64Img02)
        $imgStream02 = New-Object IO.MemoryStream($imgBytes, 0, $imgBytes.Length)
        $BotaoImg.Image = [System.Drawing.Image]::FromStream($imgStream02)
    
        $somCoruja = $PSScriptRoot+"\Wav\coruja.wav"
        (new-object Media.SoundPlayer $somCoruja).play();
    }    
  }

  ### BotaoImg_MouseLeave Event Handler
  Function BotaoImg_MouseLeave() {
    If ($BotaoImg.Image -ne $null) {
        $base64Img01 = ""
        $imgBytes = [Convert]::FromBase64String($base64Img01)
        $imgStream01 = New-Object IO.MemoryStream($imgBytes, 0, $imgBytes.Length)
        $BotaoImg.Image = [System.Drawing.Image]::FromStream($imgStream01)
    }
  }

  ### BotaoImg_Click Event Handler
  Function BotaoImg_Click() {
    If ($BotaoImg.Image -eq $null) {
        $base64Img01 = ""
        $imgBytes = [Convert]::FromBase64String($base64Img01)
        $imgStream01 = New-Object IO.MemoryStream($imgBytes, 0, $imgBytes.Length)
        $BotaoImg.Image = [System.Drawing.Image]::FromStream($imgStream01)
        $BotaoImg.Text = $null
    }
    Else {
        $BotaoImg.Image = $null
        $BotaoImg.Text = "CORUJA"
        [System.Media.SystemSounds]::Beep.Play();
    }
  }

#################################################################
_Main


